define-macro.code:
if (isset($_SESSION['dude-edit']) && $_SESSION['dude-edit'] == 'allow') {
	$m_file = urlencode();
	if (is_array())
		$m_coords = urlencode(join(',',));
	else
		$m_coords = '';
	$m_path = urlencode();
	$m_title = urlencode();
	$m_desc = urlencode();
	$m_project = urlencode(PROJECT_NAME);
	;
}
